mapper.update:
UPDATE
	checklist
SET
	name = #{name}
WHERE
	no = #{checklistno}

UPDATE
	checkitem
SET
	checked = #{checked}
WHERE
	no = #{checkitemno}

UPDATE
	checkitem
SET
	name = #{name}
WHERE
	no = #{checkitemno}

mapper.select:
SELECT  no, regdate, cardno, content
FROM boardcomment
WHERE cardno = #{cardNo}

SELECT no, name, cardno, regdate
FROM checklist
WHERE cardno = #{cardNo}

SELECT cardno, teamno
FROM tagmember
WHERE checklistno = #{checklistNo}

mapper.update:
UPDATE boardcard
SET info = #{info}
WHERE no=#{no} AND listno = #{listNo} AND boardno = #{boardNo}

mapper.select:
SELECT no, userfilename, cardno, boardno, regdate, archived
FROM attachment
WHERE no = #{attachmentno}

mapper.update:
UPDATE boardcard
SET archived = '1'
WHERE no = #{cardno}

UPDATE boardcard
SET archived = '0'
WHERE no = #{cardno}

mapper.select:
select no 
from teamlist
where memberno = #{tagNo} AND boardno=#{boardNo}

select distinct m.* 
from member m , teamlist t, tagmember g 
where m.no=t.memberno and t.no=g.teamno and g.cardno=#{cardno}
