mapper.select:
SELECT  no, regdate, cardno, content
FROM boardcomment
WHERE cardno = #{cardNo}

SELECT no, userfilename, cardno, boardno, regdate, archived
FROM attachment
WHERE cardno = #{cardNo} AND bardno = #{boardNo}

SELECT no, name, cardno, regdate
FROM checklist
WHERE cardno = #{cardNo}

SELECT no, name, checked, checklistno
FROM checkitem
WHERE checklistno = #{checklistNo}

SELECT cardno, teamno
FROM tagmember
WHERE checklistno = #{checklistNo}
