	member
SET
	username=#{userName}, fullname=#{fullName}
WHERE
	no = #{no}

UPDATE
	member
SET
	notification = #{notification}
WHERE
	no = #{no}

UPDATE
	member
SET
	iconpath = #{iconpath}
WHERE
	no = #{no}

UPDATE
	member
SET
	password = #{password}
WHERE
	no = #{memberNo}
